import React, { Component } from 'react'

import {Switch,Redirect,Route,BrowserRouter} from "react-router-dom"


import routers from "./routerConfig"
function RootRouter(){
    let routeArr = routers.filter(item=>!item.to)//不包含重定向
    let redirectArr = router.filter(item= item.to)//重定向
    return <BrowserRouter>
        <Switch>
            {
                routeArr && routeArr.map((item,index)=>{
                    return (
                        <Route path={item.path} component={item.component}></Route>
                    )
                })
            }
        </Switch>
    </BrowserRouter>
}
export default RootRouter;
