import React, { Component } from 'react'
import Axios from 'axios';
import "../../../mock/mock.js"
export default class Diancan extends Component {
    state={
        list:[],
        ind:0
    }
    componentDidMount(){
        Axios.get('/getlist').then(res=>{
            console.log(res.data.goods);
            this.setState({list:res.data.goods})
        })
    }
    handClick = (ind)=>{
        this.setState({ind})
    }
    render() {
        let { list , ind } = this.state;
        return (
            <div className="diancan">
                {/* 左侧列表导航 */}
                <div className="left">
                    {
                        list && list.map((item,index)=>{
                        return <p key={index} onClick={this.handClick.bind(this,index)} className={index==ind?"active":""}>{item.name}</p>
                        })
                    }
                </div>
                {/* 右侧数据列表 */}
                <div className="right">
                    <div className="picTxt">
                        {
                            list && list.map((item,index)=>{
                                return (
                                    <div className="item" key={index}>
                                        <h3>{item.name}</h3>
                                        {
                                            item.foods && item.foods.map((i,ind)=>{
                                                return (
                                                    <dl key={ind}>
                                                        <dt>
                                                            <img src={item.image} alt=""/>
                                                        </dt>
                                                        <dd>
                                                            <p>{i.name}</p>
                                                            {/* <p>{i.info}</p> */}
                                                            <p>{i.price}</p>
                                                        </dd>
                                                    </dl>
                                                )
                                            })
                                        }
                                    </div>
                                )
                            })
                        }
                        
                        
                    </div>
                </div>
            </div>
        )
    }
}
