import React, { Component } from 'react'

export default class Login extends Component {
    state={
        username:"",
        pwd:""
    }
    changeState=(e)=>{
        console.log(e.target.name)
        // this.setState({username:e.target.value})
    }
    // changePwd=(e)=>{
    //     this.setState({pwd:e.target.value})
    // }
    getstate=()=>{
        console.log(this.state)
    }
    render() {
        return (
            <div>
                <p><input type="text" placeholder="请输入用户名" value={this.state.username} name="name" onChange={this.changeState.bind(this)}/></p>
                <p><input type="text" placeholder="请输入密码" value={this.state.pwd} name="pwd" onChange={this.changeState.bind(this)}/></p>
                <p><button onClick={this.getstate.bind(this)}>登录</button></p>
            </div>
        )
    }
}
