import React, { Component } from 'react'
import Axios from 'axios';
import "../../../mock/mock.js"
import Item from "../../../components/Item"

import BScroll from "better-scroll";
export default class Diancan extends Component {
    state = {
        list: [],
        ind: 0,
        scrollH: []
    }
    componentDidMount() {
        Axios.get('/getlist').then(res => {
            console.log(res.data.goods);
            this.setState({ list: res.data.goods })
        })

        this.myScroll = new BScroll(".right", {
            probeType: 3
        })


        let { scrollH } = this.state;
        let child = Array.from(this.refs.rightlist.children);
        console.log(this.refs.rightlist.children, "111111111111")
        console.log(child, "child")//获取滚动元素的所有列表
        // child.forEach((item)=>{
        //     scrollH.push(item.scrollTop - 45);//获取每个元素滚动的距离
        // })
        // let last = child[child.length-1]
        // scrollH.push(last.scrollTop + last.offsetHeight);
        // this.setState({scrollH})

        // // 监听滚动
        // this.myScroll.on('scroll',({y})=>{
        //     let scrollY = Math.abs(y);
        //     // console.log(scrollY,"00000000")//滚动条滚动的高度
        //     for(let i=0;i<child.length;i++){
        //         if(scrollY>child[i] && scrollY<child[i+1]){
        //             console.log(i);
        //             this.setState({ind:i})
        //         }
        //     }
        // })
    }
    // 点击左侧列表
    handClick = (ind) => {
        this.setState({ ind })
        this.myScroll.scrollToElement('.title' + ind, 1000)
    }
    render() {
        let { list, ind } = this.state;
        return (
            <div className="diancan">
                {/* 左侧列表导航 */}
                <div className="left">
                    {
                        list && list.map((item, index) => {
                            return <p key={index} onClick={this.handClick.bind(this, index)} className={index == ind ? "active" : ""}>{item.name}</p>
                        })
                    }
                </div>
                {/* 右侧数据列表 */}
                <div className="right">
                    <div className="picTxt">
                        <div ref="rightlist">
                            {
                                list && list.map((item, index) => {
                                    return (
                                        <Item key={index} item={item} index={index}></Item>
                                    )
                                })
                            }
                        </div>

                    </div>
                </div>
            </div>
        )
    }
}
